export const offsetStopTimes = (stopTime: StopTime, offset: number) => {
    const newStopTime = Object.assign({}, stopTime);
    newStopTime.arrivalTimeSeconds += offset;
    newStopTime.arrival_time = secondsSinceMidnightToTimeStr(newStopTime.arrivalTimeSeconds);
    newStopTime.departureTimeSeconds += offset;
    newStopTime.departure_time = secondsSinceMidnightToTimeStr(newStopTime.departureTimeSeconds);
    return newStopTime;
}

